mapper.update:
update tb_board_gen
set board_title = #{board_title},
board_content = #{board_content},
board_date = sysdate

		, board_orifile = #{ board_orifile }
		, board_refile = #{ board_refile }


		, board_orifile = null
		, board_refile = null

where board_no = #{board_no}

update tb_board_gen
set board_count = board_count + 1
where board_no = #{ board_no }
